import 'dart:async';
import 'package:flutter/material.dart';
import 'package:instagram_ui/Authentication/login_screen.dart';

class SplashView extends StatefulWidget {
  const SplashView({Key? key}) : super(key: key);

  @override
  State<SplashView> createState() => _SplashViewState();

}
     
class _SplashViewState extends State<SplashView> {
  @override
  void initState() {
    Timer(const Duration(seconds: 5), () => Navigator.push(context, MaterialPageRoute(builder: (context) =>const LogInScreen(),)));
    super.initState();
  }
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.white,
      body: Column(
        crossAxisAlignment: CrossAxisAlignment.center,
        mainAxisAlignment: MainAxisAlignment.center,
        children: [
           Image.asset('images/ig.png',
           height: 150,
           ),
             const Text('INTSGRAM',style:  TextStyle(fontWeight: FontWeight.bold,fontSize: 30,fontStyle: FontStyle.italic,color: Colors.red),),
          const SizedBox(height: 180,),
          
          const Text('From',style: TextStyle(fontWeight: FontWeight.bold,fontSize: 30),),
          Container(
            height: 50,
            decoration: const BoxDecoration(
              image: DecorationImage(image: NetworkImage('data:image/png;base64,'))
            ),
          )
        ],
      )
    );
  }
}